import React from 'react';
import CoinbaseWalletSDK from '@coinbase/wallet-sdk';
import { BrowserProvider } from 'ethers';

const LoginPage = () => {
  const handleCoinbaseLogin = async () => {
    try {
      const APP_NAME = 'CryptoAuth';
      const CHAIN_ID = 1;

      const RPC_URL = 'https://cloudflare-eth.com';

      if (!RPC_URL || RPC_URL.includes('your_actual_project_id_here')) {
        throw new Error('üö´ You must replace RPC_URL with your actual Infura project URL!');
      }

      const coinbaseWallet = new CoinbaseWalletSDK({
        appName: APP_NAME,
        darkMode: false,
      });
      console.log('Using RPC:', RPC_URL);
      const ethereum = coinbaseWallet.makeWeb3Provider(RPC_URL, CHAIN_ID);
      const provider = new BrowserProvider(ethereum);
      const accounts = await provider.send('eth_requestAccounts', []);

      if (!accounts || !accounts.length) {
        alert('No wallet address returned.');
        return;
      }

      alert(`üü¢ Connected Wallet: ${accounts[0]}`);
    } catch (error) {
      console.error('‚ùå Wallet connection error:', error);
      alert(error.message || 'Failed to connect wallet.');
    }
  };

  return (
    <div style={{ padding: '2rem', textAlign: 'center' }}>
      <h2>Login</h2>
      <button
        onClick={handleCoinbaseLogin}
        style={{
          padding: '0.75rem 1.5rem',
          backgroundColor: '#4f46e5',
          color: 'white',
          fontSize: '1rem',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer',
        }}
      >
        Connect Coinbase Wallet
      </button>
    </div>
  );
};

export default LoginPage;
