import React from 'react';
import { CoinbaseWalletSDK } from '@coinbase/wallet-sdk';
import { BrowserProvider } from 'ethers';

const LoginPage = () => {
  const handleCoinbaseLogin = async () => {
    try {
      const APP_NAME = 'CryptoAuth';
      const CHAIN_ID = 1;  // Ensure this is an integer, not a string or object.
      const RPC_URL = 'https://mainnet.infura.io/v3/151ac0015300430ca8ec9b62f8002589'; // Your Infura URL
  
      const coinbaseWallet = new CoinbaseWalletSDK({
        appName: APP_NAME,
        appLogoUrl: 'https://example.com/logo.png', // Optional logo
        darkMode: false,
        theme: 'light',
        appChainId: CHAIN_ID,  // Pass CHAIN_ID as an integer
      });
  
      const ethereum = coinbaseWallet.makeWeb3Provider(RPC_URL, CHAIN_ID);
      const provider = new BrowserProvider(ethereum);
      const accounts = await provider.send('eth_requestAccounts', []);
      const address = accounts[0];
  
      alert(`üü¢ Connected Wallet: ${address}`);
    } catch (error) {
      console.error('‚ùå Wallet connection error:', error);
      alert('Failed to connect wallet. Check console for error.');
    }
  };
  
};

export default LoginPage;
