import React, { useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login'); // Redirect to login if not authenticated
    }
  }, [isAuthenticated, navigate]);

  if (!user) return <div>Loading...</div>;

  const userRole = user.role; // Assuming role is set in AuthContext

  return (
    <div className="home-page-container">
      <header className="home-page-header">
        <div className="logo">CryptoAuth</div>
        <div className="wallet-address">{user.address}</div>
        <button className="logout-button">Logout</button>
      </header>

      <div className="home-page-main">
        <aside className="home-page-sidebar">
          <ul>
            <li>Dashboard</li>
            {userRole === 'Admin' && <li>Manage Users</li>}
            {userRole === 'Manager' && <li>View Requests</li>}
            {userRole === 'Member' && <li>My Tasks</li>}
          </ul>
        </aside>

        <section className="home-page-content">
          {userRole === 'Admin' && (
            <div className="admin-section">
              <h3>User List</h3>
              <p>Total Users: {/* Render total number of users here */}</p>
              {/* Render the list of users with roles */}
              <button>Manage User Authorization</button>
            </div>
          )}

          {userRole === 'Manager' && (
            <div className="manager-section">
              <h3>Member Requests</h3>
              <ul>
                {/* Render member requests here */}
                <li>Request to upgrade from Member to Manager</li>
                {/* Add more requests */}
              </ul>
              <button>View New Member Requests</button>
            </div>
          )}

          {userRole === 'Member' && (
            <div className="member-section">
              <h3>Your Tasks</h3>
              <ul>
                {/* Render tasks assigned to the member */}
                <li>Task 1 - Due: 2025-03-30</li>
                <li>Task 2 - Due: 2025-04-01</li>
                {/* Add more tasks */}
              </ul>
              <button>Request Upgrade Status</button>
            </div>
          )}
        </section>
      </div>
    </div>
  );
};

export default HomePage;
