import React from 'react';
import CoinbaseWalletSDK from '@coinbase/wallet-sdk';
import { BrowserProvider } from 'ethers';

const LoginPage = () => {
  const handleCoinbaseLogin = async () => {
    try {
      const APP_NAME = 'CryptoAuth';
      const INFURA_ID = 'YOUR_INFURA_PROJECT_ID';
      const RPC_URL = `https://mainnet.infura.io/v3/151ac0015300430ca8ec9b62f8002589`;
      const CHAIN_ID = 1;

      const coinbaseWallet = new CoinbaseWalletSDK({
        appName: APP_NAME,
        darkMode: false,
      });

      // STEP 1: Get wallet provider
      const ethereum = coinbaseWallet.makeWeb3Provider(RPC_URL, CHAIN_ID);

      if (!ethereum) {
        alert('Coinbase provider not available.');
        return;
      }

      // STEP 2: Request connection
      const provider = new BrowserProvider(ethereum);
      const accounts = await provider.send('eth_requestAccounts', []);

      if (!accounts.length) {
        alert('No account found.');
        return;
      }

      const address = accounts[0];
      alert(`üü¢ Connected: ${address}`);
    } catch (error) {
      console.error('‚ùå Wallet connection error:', error);
      alert('Connection failed. Check console for details.');
    }
  };

  return (
    <div style={{ padding: '2rem', textAlign: 'center' }}>
      <h2>Login</h2>
      <button
        onClick={handleCoinbaseLogin}
        style={{
          padding: '0.75rem 1.5rem',
          backgroundColor: '#4f46e5',
          color: 'white',
          fontSize: '1rem',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer',
        }}
      >
        Connect Coinbase Wallet
      </button>
    </div>
  );
};

export default LoginPage;
