import React from 'react';
import CoinbaseWalletSDK from '@coinbase/wallet-sdk';
import { BrowserProvider } from 'ethers';


const LoginPage = () => {
  const handleCoinbaseLogin = async () => {
    try {
      const APP_NAME = 'CryptoAuth';
      const APP_LOGO_URL = ''; // optional
      const ETH_JSONRPC_URL = 'https://mainnet.infura.io/v3/151ac0015300430ca8ec9b62f8002589'; // placeholder
      const CHAIN_ID = 1;

      const coinbaseWallet = new CoinbaseWalletSDK({
        appName: APP_NAME,
        appLogoUrl: APP_LOGO_URL,
        darkMode: false,
      });

      const ethereum = coinbaseWallet.makeWeb3Provider(ETH_JSONRPC_URL, CHAIN_ID);
      const provider = new ethers.providers.Web3Provider(ethereum);
      const accounts = await provider.send('eth_requestAccounts', []);
      const address = accounts[0];

      console.log('üü¢ Connected Coinbase Wallet:', address);

      // TODO: save to AuthContext
    } catch (error) {
      console.error('‚ùå Coinbase wallet connection failed:', error);
    }
  };

  return (
    <div style={{ padding: '2rem', textAlign: 'center' }}>
      <h2>Login</h2>
      <button
        onClick={handleCoinbaseLogin}
        style={{
          padding: '0.75rem 1.5rem',
          backgroundColor: '#4f46e5',
          color: 'white',
          fontSize: '1rem',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer',
        }}
      >
        Connect Coinbase Wallet
      </button>
    </div>
  );
};

export default LoginPage;
