import React, { createContext, useContext, useState } from 'react';
import { CONTRACT_ADDRESS, CONTRACT_ABI } from '../lib/contractInfo';
import { ethers } from 'ethers';
import { useEffect } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);  // Store user data (address)
  const [hasNFT, setHasNFT] = useState(null);

  // Login function
  const login = (userData) => {
    setUser(userData);  // Set user data when logging in
  };

  // Logout function
  const logout = () => {
    setUser(null);  // Reset user data when logging out
  };

  const checkNFT = async (walletAddress) => {
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const contract = new ethers.Contract(CONTRACT_ADDRESS, CONTRACT_ABI, provider);
      const balance = await contract.balanceOf(walletAddress);
      setHasNFT(balance > 0n);
    } catch (err) {
      console.error('Error checking NFT:', err);
      setHasNFT(false);
    }
  };

  const isAuthenticated = !!user;

  useEffect(() => {
    if (user && isAuthenticated) {
      checkNFT(user.address);
    }
  }, [user, isAuthenticated]);

  return (
    <AuthContext.Provider value={{ user, login, logout, isAuthenticated, hasNFT }}>
      {children}
    </AuthContext.Provider>
  );

};

export const useAuth = () => useContext(AuthContext);
