import React from 'react';
import CoinbaseWalletSDK from '@coinbase/wallet-sdk';
import { BrowserProvider } from 'ethers';

const LoginPage = () => {
  const handleCoinbaseLogin = async () => {
    try {
      const APP_NAME = 'CryptoAuth';
      const CHAIN_ID = 1;
      const RPC_URL = 'https://mainnet.infura.io/v3/YOUR_INFURA_PROJECT_ID'; // full URL

      const coinbaseWallet = new CoinbaseWalletSDK({
        appName: APP_NAME,
        appLogoUrl: 'https://example.com/logo.png', // Optional but helps
        darkMode: false
      });

      // ‚úÖ Wrap both in one config object (Coinbase Wallet requires this shape)
      const ethereum = coinbaseWallet.makeWeb3Provider(RPC_URL, {
        chainId: CHAIN_ID,
        jsonRpcUrl: RPC_URL
      });

      const provider = new BrowserProvider(ethereum);
      const accounts = await provider.send('eth_requestAccounts', []);
      const address = accounts[0];

      alert(`üü¢ Connected Wallet: ${address}`);
    } catch (error) {
      console.error('‚ùå Wallet connection error:', error);
      alert('Connection failed. Check console for details.');
    }
  };

  return (
    <div style={{ padding: '2rem', textAlign: 'center' }}>
      <h2>Login</h2>
      <button
        onClick={handleCoinbaseLogin}
        style={{
          padding: '0.75rem 1.5rem',
          backgroundColor: '#4f46e5',
          color: 'white',
          fontSize: '1rem',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer',
        }}
      >
        Connect Coinbase Wallet
      </button>
    </div>
  );
};

export default LoginPage;
